import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { getAnalytics } from '../features/url/urlSlice';
import { Bar, Line, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

function Analytics() {
  const { urlCode } = useParams();
  const dispatch = useDispatch();
  const { analytics, isLoading } = useSelector((state) => state.url);
  
  useEffect(() => {
    dispatch(getAnalytics(urlCode));
  }, [dispatch, urlCode]);
  
  if (isLoading) {
    return <div className="text-center py-8">Loading analytics...</div>;
  }
  
  if (!analytics) {
    return <div className="text-center py-8">No analytics data found</div>;
  }
  
  // Prepare data for charts
  const clicksOverTimeData = {
    labels: analytics.clicksOverTime?.map(item => item._id) || [],
    datasets: [{
      label: 'Clicks',
      data: analytics.clicksOverTime?.map(item => item.count) || [],
      borderColor: 'rgb(59, 130, 246)',
      backgroundColor: 'rgba(59, 130, 246, 0.5)'
    }]
  };
  
  const devicesData = {
    labels: analytics.devices?.map(item => item._id || 'Unknown') || [],
    datasets: [{
      label: 'Devices',
      data: analytics.devices?.map(item => item.count) || [],
      backgroundColor: [
        'rgba(255, 99, 132, 0.5)',
        'rgba(54, 162, 235, 0.5)',
        'rgba(255, 206, 86, 0.5)'
      ],
      borderColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)'
      ],
      borderWidth: 1
    }]
  };
  
  const browsersData = {
    labels: analytics.browsers?.map(item => item._id || 'Unknown') || [],
    datasets: [{
      label: 'Browsers',
      data: analytics.browsers?.map(item => item.count) || [],
      backgroundColor: [
        'rgba(153, 102, 255, 0.5)',
        'rgba(75, 192, 192, 0.5)',
        'rgba(255, 159, 64, 0.5)'
      ],
      borderColor: [
        'rgba(153, 102, 255, 1)',
        'rgba(75, 192, 192, 1)',
        'rgba(255, 159, 64, 1)'
      ],
      borderWidth: 1
    }]
  };
  
  return (
    <div>
      <div className="mb-8">
        <h1 className="text-2xl font-bold mb-2">Analytics for: {analytics.url?.shortUrl}</h1>
        <p className="text-gray-600">
          Original URL: <a href={analytics.url?.longUrl} target="_blank" rel="noopener noreferrer" className="text-blue-600">
            {analytics.url?.longUrl}
          </a>
        </p>
        <div className="mt-4 flex space-x-4">
          <div className="bg-blue-100 text-blue-800 px-4 py-2 rounded-lg">
            Total Clicks: <span className="font-bold">{analytics.totalClicks}</span>
          </div>
          <div className={`px-4 py-2 rounded-lg ${
            analytics.url?.expirationDate && new Date(analytics.url.expirationDate) < new Date() 
              ? 'bg-red-100 text-red-800' 
              : 'bg-green-100 text-green-800'
          }`}>
            Status: <span className="font-bold">
              {analytics.url?.expirationDate && new Date(analytics.url.expirationDate) < new Date() 
                ? 'Expired' 
                : 'Active'}
            </span>
          </div>
        </div>
      </div>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4">Clicks Over Time</h2>
          <Line data={clicksOverTimeData} />
        </div>
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4">Devices</h2>
          <Pie data={devicesData} />
        </div>
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4">Browsers</h2>
          <Pie data={browsersData} />
        </div>
      </div>
      
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <h2 className="text-xl font-semibold p-6 border-b">Recent Clicks</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Timestamp
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  IP Address
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Device
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Browser
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {analytics.clicks?.map((click) => (
                <tr key={click._id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(click.timestamp).toLocaleString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {click.ipAddress}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {click.deviceType}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {click.browser}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Analytics;
